import { validate, getCheckDigit } from 'rut.js'
import cogoToast from "cogo-toast";
import { useEffect, useState } from "react";
import "./index.css";

const initialState = {
  rut: "",
  dv: "",
  isLoading: false,
};

function App() {
  const [state, setState] = useState(initialState);

  const { rut, dv, isLoading } = state;

  function setRut(event) {
    const { value } = event.target;

    if (value !== null && isNaN(value)) {
      return;
    }

    setState((prevState) => ({
      ...prevState,
      rut: value,
    }));
  }

  function isRutValid() {
    if (!rut) {
      return false
    }

    const checkDigit = getCheckDigit(rut)
    const rutToValidate = `${rut}-${checkDigit}`
    console.log(rutToValidate, validate(rutToValidate))

    return validate(rutToValidate)
  }

  async function handleCalculateClick() {
    try {
      if (!rut) {
        return cogoToast.error(
          "Debes ingresar un rut para calcular el digito verificador."
        );
      }

      if (!isRutValid()) {
        return cogoToast.error(
          "El rut ingresado no es válido."
        );
      }

      setState((prevState) => ({
        ...prevState,
        isLoading: true,
      }));

      const response = await fetch(
        `${process.env.REACT_APP_API_URI}/getDv/${rut}`
      );
      const jsonResponse = await response.json();

      if (jsonResponse && jsonResponse.data) {
        setState((prevState) => ({
          ...prevState,
          dv: jsonResponse.data,
          isLoading: false,
        }));
        return cogoToast.success("Digito verificador calculado con éxito!");
      }
    } catch (error) {
      setState((prevState) => ({
        ...prevState,
        isLoading: false,
      }));

      cogoToast.error(error);
      console.log(error);
    }
  }

  return (
    <div className="container">
      <div className="header">
        <h1 className="header__text">Calculadora de digito verificador</h1>
      </div>
      <div className="input-group-container">
        <div className="input-group">
          <div className="main-rut-group">
            <label htmlFor="main-rut">
              RUT
              <span className="sin-dv-span">(sin digito verificador)</span>
            </label>
            <input
              name="rut"
              value={rut}
              className="main-rut"
              id="main-rut"
              onChange={setRut}
              autoComplete="off"
            />
          </div>
          <div className="dv-group">
            <label htmlFor="dv">DV</label>
            <input className="dv" id="dv" value={dv} disabled />
          </div>
        </div>
        <button className="calculate-button" onClick={handleCalculateClick}>
          {isLoading ? "Cargando..." : "Calcular DV"}
        </button>
      </div>

    </div>
  );
}

export default App;
