import cogoToast from "cogo-toast";
import { useEffect, useState } from "react";
import "./index.css";

const initialState = {
  namesInput: "",
  names: "",
  lastNames: "",
  isLoading: false,
};

function App() {
  const [state, setState] = useState(initialState);

  const { namesInput, names, lastNames, isLoading } = state;

  function setField(event) {
    const { value } = event.target;

    if (value !== null && !isNaN(value)) {
      return;
    }

    setState((prevState) => ({
      ...prevState,
      namesInput: value,
    }));
  }

  async function handleCalculateClick() {
    try {
      if (!namesInput) {
      setState((prevState) => ({
        ...prevState,
        namesInput: '',
      }));

        return cogoToast.error(
          "Debes ingresar nombres y apellidos para realizar el split."
        );
      }

      setState((prevState) => ({
        ...prevState,
        isLoading: true,
      }));

      const response = await fetch(
        `${process.env.REACT_APP_API_URI}/getDv.php/${rut}`
      );
      const jsonResponse = await response.json();

      if (jsonResponse && jsonResponse.data) {
        setState((prevState) => ({
          ...prevState,
          dv: jsonResponse.data,
          isLoading: false,
        }));
        return cogoToast.success("Digito verificador calculado con éxito!");
      }
    } catch (error) {
      setState((prevState) => ({
        ...prevState,
        dv: '',
        isLoading: false,
      }));

      cogoToast.error(error);
      console.log(error);
    }
  }

  return (
    <div className="container">
      <div className="header">
        <h1 className="header__text">Split de nombres y apellidos</h1>
      </div>
      <div className="input-group-container">
        <div className="input-group">
          <div className="main-rut-group">
            <label htmlFor="main-rut">
              Ingrese nombres y apellidos
            </label>
            <input
              name="rut"
              placeholder="Ejemplo: Juan José Rodriguez Hernandez"
              value={rut}
              className="main-rut"
              id="main-rut"
              onChange={setRut}
              autoComplete="off"
            />
          </div>
        </div>
        <button className="calculate-button" onClick={handleCalculateClick}>
          {isLoading ? "Cargando..." : "Realizar Split"}
        </button>
      </div>

    </div>
  );
}

export default App;
